import React from 'react';
import { useState, useEffect } from "react";
import {
  IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonLoading,
  IonButton
} from '@ionic/react';
import useLocalStorage from '../components/LocalStorage'
import './Tab2.css';

interface Chat {
  id: string
  createdAt: Date
  userId: string
}

const Tab2: React.FC = () => {
  const [chats, setChats] = useState<Chat[]>([]);
  const [showLoading, setShowLoading] = useState<boolean>(true);
  const [api, setAPI] = useLocalStorage('api', "https://localhost:5001");
  const [userid, setUserID] = useLocalStorage('userid', "anonymous");

  const fetchChats = async () => {
    fetch(`${api}/api/chat/all`)
      .then(res => res.json()).then(setChats).finally(() => setShowLoading(false))
  };

  useEffect(() => {
    fetchChats()
  }, []/*[chats]*/)

  const makeLink = (chat: Chat) => { return "/tab3/" + chat.id }

  const newChat = async () => {
    const newChat = {
      userId: userid
    }
    const options = {
      method: 'POST',
      body: JSON.stringify(newChat),
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }
    fetch(`${api}/api/chat/new/`, options)
      .then(res => res.json())
      .then(_ => fetchChats())
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle class="tab-pos">Tab 2</IonTitle> <IonLabel class="right">{userid}</IonLabel>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        <IonLoading
          isOpen={showLoading}
          onDidDismiss={() => setShowLoading(false)}
          message={'Loading...'}
        />
        <IonList>
          {chats.map((item, index) => (
            <IonItem key={index} routerLink={makeLink(item)}>
              <IonLabel>
                <h1>{item.createdAt}</h1>
                <p>{item.userId}</p>
              </IonLabel>
            </IonItem>
          ))}
        </IonList>
        <IonToolbar>
          <IonButton expand="block" onClick={() => newChat()}>Create New</IonButton>
        </IonToolbar>
      </IonContent>
    </IonPage>
  );
};

export default Tab2;
